t.script:
                $(document).ready(function() {
                // Select2 initialization
                $('.js-example-basic-single').select2({
                placeholder: "Select an option",
                allowClear: true
                });
                $('.js-example-basic-multiple').select2({
                placeholder: "Select multiple options",
                allowClear: true,
                width: '100%'
                });

                // Auto-populate task name when project changes
                $('select[name="project_id"]').change(function() {
                var projectId = $(this).val();
                if (projectId) {
                var projectName = $(this).find('option:selected').text();
                $('input[name="name"]').val(projectName);
                }
                });

                // Dynamic Select2 for assignees with auto-hide
                function setupDynamicSelect2(selector) {
                const selectElement = $(selector);
                if (!selectElement.length) {
                return;
                }
                selectElement.select2({
                placeholder: "Select an option",
                allowClear: true,
                width: '100%',
                templateResult: function(data) {
                try {
                var selectedIds = selectElement.val() || [];
                var dataId = (data && typeof data.id !== 'undefined' && data.id !== null) ?
                data.id.toString() : null;
                if (dataId && selectedIds.indexOf(dataId) !== -1) {
                return null;
                }
                return data && (data.text || (data.element && $(data.element).text())) || '';
                } catch (err) {
                return data && (data.text || '') || '';
                }
                }
                });
                }

                // Initialize for assignee selectors
                setupDynamicSelect2('select[name="z_head_assignes_ids"]');
                setupDynamicSelect2('select[name="z_member_assignes_ids"]');

                // Group by handler with icon toggle
                $(document).on('click', '[data-bs-target^="#collapse_"]', function(e) {
                e.preventDefault();
                var target = $(this).attr('data-bs-target');
                var icon = $(this).find('i');
                var collapse = $(target);

                if (collapse.hasClass('show')) {
                collapse.removeClass('show');
                icon.removeClass('fa-caret-down').addClass('fa-caret-right');
                } else {
                collapse.addClass('show');
                icon.removeClass('fa-caret-right').addClass('fa-caret-down');
                }
                });

                // CSRF token
                const csrf_token = $('input[name="csrf_token"]').val() || '';

                // Timer UI variables
                const $timerBadge = $('#runningTimerBadge');
                let timerInterval = null;
                let isPaused = false;

                // FIXED: Format datetime untuk display WIB
                function formatUtcToWib(utcStr) {
                if (!utcStr) return '';

                try {
                const utcDate = new Date(utcStr);
                // Add 7 hours for WIB (UTC+7)
                const wibDate = new Date(utcDate.getTime() + (7 * 60 * 60 * 1000));

                const month = String(wibDate.getMonth() + 1).padStart(2, '0');
                const day = String(wibDate.getDate()).padStart(2, '0');
                const year = wibDate.getFullYear();
                const hours = String(wibDate.getHours()).padStart(2, '0');
                const minutes = String(wibDate.getMinutes()).padStart(2, '0');
                const seconds = String(wibDate.getSeconds()).padStart(2, '0');

                return month + '/' + day + '/' + year + ' ' + hours + ':' + minutes + ':' + seconds;
                } catch (e) {
                console.error('Error converting datetime:', e);
                return utcStr;
                }
                }

                // FIXED: Format hours to hours and minutes
                function formatHoursToHM(hours) {
                if (!hours || hours === 0) return '0m';

                const totalMinutes = Math.round(hours * 60);
                const h = Math.floor(totalMinutes / 60);
                const m = totalMinutes % 60;

                if (h === 0) {
                return m + 'm';
                } else if (m === 0) {
                return h + 'h';
                } else {
                return h + 'h ' + m + 'm';
                }
                }

                // Apply timezone conversion to timesheet datetime elements
                $('.timesheet-datetime').each(function() {
                const utcStr = $(this).attr('data-utc');
                if (utcStr) {
                const wibStr = formatUtcToWib(utcStr);
                $(this).text(wibStr);
                }
                });

                // Apply hours formatting to time spent elements
                $('.timesheet-hours').each(function() {
                const hoursVal = parseFloat($(this).attr('data-hours'));
                if (!isNaN(hoursVal)) {
                const formattedTime = formatHoursToHM(hoursVal);
                $(this).text(formattedTime);
                }
                });

                // FIXED: Timer function yang BENAR-BENAR mulai dari 00:00:00
                function startUiTimer(startIsoStr) {
                if (!startIsoStr) return;

                console.log('=== TIMER INITIALIZATION ===');
                console.log('Raw timer start string from server:', startIsoStr);

                let start;
                try {
                // CRITICAL FIX: Parse sebagai UTC dengan explicit Z suffix
                let utcString = startIsoStr;
                if (!utcString.includes('Z') && !utcString.includes('+')) {
                utcString = startIsoStr + 'Z';
                }

                start = new Date(utcString);

                if (isNaN(start.getTime())) {
                console.error('Invalid start time after parsing:', utcString);
                return;
                }

                console.log('Parsed start time (UTC):', start.toISOString());
                console.log('Current browser time (UTC):', new Date().toISOString());

                // Check if time difference is reasonable (not 7+ hours off)
                const now = new Date();
                const diffHours = (now.getTime() - start.getTime()) / (1000 * 60 * 60);
                console.log('Time difference in hours:', diffHours);

                if (diffHours > 24 || diffHours
                <
                -1) {
                console.warn('Suspicious time difference detected:', diffHours, 'hours');
                }

                } catch (e) {
                console.error('Error parsing timer start time:', startIsoStr, e);
                return;
                }

                $timerBadge.removeClass('d-none');
                if (timerInterval) clearInterval(timerInterval);

                function pad(n) { return n
                <
                10 ? '0' + n : n; }

                function updateTimer() {
                if (isPaused) return; // Don't update when paused

                const now = new Date();

                // CRITICAL: Calculate ONLY the time difference without timezone confusion
                let diffMs = now.getTime() - start.getTime();
                let diffSeconds = Math.max(0, Math.floor(diffMs / 1000));

                const hours = Math.floor(diffSeconds / 3600);
                const minutes = Math.floor((diffSeconds % 3600) / 60);
                const seconds = diffSeconds % 60;

                const timeStr = pad(hours) + ':' + pad(minutes) + ':' + pad(seconds);
                $timerBadge.text(timeStr);

                // Debug every minute
                if (diffSeconds % 60 === 0 && diffSeconds > 0) {
                console.log('Timer update: ' + timeStr + ' (' + diffSeconds + 's elapsed)');
                console.log('Start: ' + start.toISOString() + ', Now: ' + now.toISOString());
                }
                }

                // Start immediately
                updateTimer();
                timerInterval = setInterval(updateTimer, 1000);

                console.log('Timer started successfully from 00:00:00');
                }

                // Initialize timer if running
                const startIso = $timerBadge.data('start');
                if (startIso) {
                console.log('Found existing timer data:', startIso);
                startUiTimer(startIso);
                } else {
                console.log('No active timer found');
                }

                // Function untuk refresh timesheet table
                function refreshTimesheetTable() {
                setTimeout(function() {
                window.location.reload();
                }, 1000);
                }

                // Timesheet popup handlers
                function openTimesheetPopup(taskId, timesheetId) {
                timesheetId = timesheetId || null;
                $('#current_task_id').val(taskId || $('#current_task_id').val());
                if (timesheetId) {
                $.get('/portal/task/' + taskId + '/timesheet/' + timesheetId, function(timesheet) {
                if (timesheet.error) {
                alert('Error: ' + timesheet.error);
                return;
                }
                populateTimesheetForm(timesheet);
                }).fail(function() {
                alert('Failed to load timesheet data');
                });
                } else {
                $('#timesheetForm')[0].reset();
                $('#timesheet_id').val('');
                }
                $('#timesheetModal').modal('show');
                }

                function populateTimesheetForm(timesheet) {
                $('#timesheet_id').val(timesheet.id);
                $('#timesheet_start_date').val(timesheet.start_date || '');
                $('#timesheet_end_date').val(timesheet.end_date || '');
                $('#timesheet_description').val(timesheet.description);
                $('#timesheet_employee').val(timesheet.employee_id).trigger('change');
                $('#timesheet_hours').val(timesheet.hours);
                }

                $(document).on('click', '.add-timesheet-btn, .edit-timesheet-btn', function() {
                var taskId = $(this).data('task-id');
                var timesheetId = $(this).data('timesheet-id');
                openTimesheetPopup(taskId, timesheetId);
                });

                $('#saveTimesheetBtn').click(function() {
                var taskId = $('#current_task_id').val();
                var formData = new FormData($('#timesheetForm')[0]);
                formData.append('csrf_token', csrf_token);

                $.ajax({
                url: '/portal/task/' + taskId + '/timesheet',
                type: 'POST',
                data: formData,
                processData: false,
                contentType: false,
                dataType: 'json',
                success: function(result) {
                if (result.success) {
                $('#timesheetModal').modal('hide');
                refreshTimesheetTable();
                } else {
                alert('Error: ' + (result.error || 'Unknown error'));
                }
                },
                error: function() {
                alert('Failed to save timesheet');
                }
                });
                });

                // Subtask popup handlers
                function openSubtaskPopup(taskId, subtaskId) {
                subtaskId = subtaskId || null;
                $('#current_task_id').val(taskId || $('#current_task_id').val());

                if (subtaskId) {
                $.get('/portal/task/' + taskId + '/subtask/' + subtaskId, function(subtask) {
                if (subtask.error) {
                alert('Error: ' + subtask.error);
                return;
                }
                populateSubtaskForm(subtask);
                }).fail(function() {
                alert('Failed to load subtask data');
                });
                } else {
                $('#subtaskForm')[0].reset();
                $('#subtask_id').val('');
                // FIXED: Auto-populate dengan master task jika ada
                var masterTaskSelect = $('#subtask_master_task');
                if (masterTaskSelect.length > 0 && masterTaskSelect.find('option').length > 1) {
                // Select first available master task sebagai default
                masterTaskSelect.val(masterTaskSelect.find('option:eq(1)').val()).trigger('change');
                }
                }
                $('#subtaskModal').modal('show');
                }

                function populateSubtaskForm(subtask) {
                $('#subtask_id').val(subtask.id);
                $('#subtask_name').val(subtask.name);
                $('#subtask_master_task').val(subtask.z_master_task_id).trigger('change');
                $('#subtask_status').val(subtask.z_project_task_state).trigger('change');
                $('#subtask_head_assignes').val(subtask.z_head_assignes_ids).trigger('change');
                $('#subtask_member_assignes').val(subtask.z_member_assignes_ids).trigger('change');
                }

                $(document).on('click', '.add-subtask-btn, .edit-subtask-btn', function() {
                var taskId = $(this).data('task-id');
                var subtaskId = $(this).data('subtask-id');
                openSubtaskPopup(taskId, subtaskId);
                });

                $('#saveSubtaskBtn').click(function() {
                var taskId = $('#current_task_id').val();
                var formData = new FormData($('#subtaskForm')[0]);
                formData.append('csrf_token', csrf_token);

                $.ajax({
                url: '/portal/task/' + taskId + '/subtask',
                type: 'POST',
                data: formData,
                processData: false,
                contentType: false,
                dataType: 'json',
                success: function(result) {
                if (result.success) {
                $('#subtaskModal').modal('hide');
                // FIXED: Redirect ke parent subtasks list jika dari parent
                var currentUrl = window.location.href;
                if (currentUrl.includes('/parent/')) {
                location.reload();
                } else {
                location.reload();
                }
                } else {
                alert('Error: ' + (result.error || 'Unknown error'));
                }
                },
                error: function() {
                alert('Failed to save subtask');
                }
                });
                });

                // Invoice plan popup handlers
                function openInvoicePlanPopup(taskId, invoicePlanId) {
                invoicePlanId = invoicePlanId || null;
                $('#current_task_id').val(taskId || $('#current_task_id').val());

                if (invoicePlanId) {
                $.get('/portal/task/' + taskId + '/invoice-plan/' + invoicePlanId, function(invoicePlan) {
                if (invoicePlan.error) {
                alert('Error: ' + invoicePlan.error);
                return;
