                // CRITICAL FIX: Timer Start/Stop dengan proper UTC handling dan PAUSE functionality
                $(document).on('click', '#btnTimeStart', function(e) {
                e.preventDefault();
                const taskId = $(this).data('task-id');
                const isResuming = isPaused;

                console.log('=== TIMER START/RESUME REQUEST ===');
                console.log('Task ID:', taskId);
                console.log('Is Resuming:', isResuming);
                console.log('Current browser UTC time:', new Date().toISOString());

                const action = isResuming ? 'resume' : 'start';

                $.post('/portal/task/' + taskId + '/timer', {
                action: action,
                csrf_token: csrf_token
                }, function(r) {
                console.log('=== TIMER START/RESUME RESPONSE ===');
                console.log('Full server response:', r);

                if (r.success) {
                console.log('Server returned start_at:', r.start_at);

                // Clear any existing timer first
                if (timerInterval) {
                clearInterval(timerInterval);
                timerInterval = null;
                }

                // Start/Resume timer with server time
                startUiTimer(r.start_at);
                isPaused = false;

                // Update button states
                $('#btnTimeStart').prop('disabled', true).find('span').text('Time Start');
                $('#btnTimePause').prop('disabled', false).show();
                $('#btnTimeStop').prop('disabled', false);

                // Update timer badge
                $timerBadge.removeClass('bg-warning').addClass('bg-primary').text('00:00:00');

                // Reload page after 2 seconds if starting fresh
                if (!isResuming) {
                setTimeout(function() {
                location.reload();
                }, 2000);
                }
                } else {
                alert('Error: ' + (r.error || 'Unknown error'));
                }
                }, 'json').fail(function(xhr, status, error) {
                console.error('Timer start/resume request failed:', status, error);
                if (xhr.responseText) {
                console.error('Response text:', xhr.responseText);
                }
                alert('Failed to start/resume timer: ' + error);
                });
                });

                // NEW: Pause button handler
                $(document).on('click', '#btnTimePause', function(e) {
                e.preventDefault();
                const taskId = $(this).data('task-id');

                console.log('=== TIMER PAUSE REQUEST ===');
                console.log('Task ID:', taskId);

                $.post('/portal/task/' + taskId + '/timer', {
                action: 'pause',
                csrf_token: csrf_token
                }, function(r) {
                console.log('=== TIMER PAUSE RESPONSE ===');
                console.log('Server response:', r);

                if (r.success) {
                isPaused = true;

                // Update UI untuk paused state
                $('#btnTimeStart').prop('disabled', false).find('span').text('Resume');
                $('#btnTimePause').prop('disabled', true).hide();
                $('#btnTimeStop').prop('disabled', false);

                // Update timer badge untuk indicate paused
                $timerBadge.removeClass('bg-primary').addClass('bg-warning');

                alert('Timer paused successfully');
                } else {
                alert('Error: ' + (r.error || 'Unknown error'));
                }
                }, 'json').fail(function(xhr, status, error) {
                console.error('Timer pause request failed:', status, error);
                alert('Failed to pause timer: ' + error);
                });
                });

                $(document).on('click', '#btnTimeStop', function(e) {
                e.preventDefault();
                $('#timerStopModal').modal('show');
                });

                // FIXED: Timer Stop dengan validation dan attachment (XML Compatible)
                $('#confirmStopBtn').click(function() {
                const taskId = $(this).data('task-id');
                const desc = $('#timerStopDescription').val().trim();

                // VALIDATION: Description wajib diisi
                if (!desc) {
                alert('Description is required when stopping timer');
                $('#timerStopDescription').focus();
                return;
                }

                console.log('=== TIMER STOP REQUEST ===');
                console.log('Task ID:', taskId);
                console.log('Description:', desc);

                // FIXED: Handle file uploads dengan FormData
                const formData = new FormData();
                formData.append('action', 'stop');
                formData.append('description', desc);
                formData.append('csrf_token', csrf_token);

                // FIXED: Add uploaded files dengan key yang benar
                const fileInput = $('#timerAttachments')[0];
                if (fileInput.files && fileInput.files.length > 0) {
                for (let i = 0; i< fileInput.files.length; i++) {
                formData.append('attachments', fileInput.files[i]);
                }
                console.log('Adding ' + fileInput.files.length + ' file(s) to upload');
                }

                // Show loading state
                $('#confirmStopBtn').prop('disabled', true).text('Stopping...');

                $.ajax({
                url: '/portal/task/' + taskId + '/timer',
                type: 'POST',
                data: formData,
                processData: false,
                contentType: false,
                dataType: 'json',
                success: function(r) {
                console.log('=== TIMER STOP RESPONSE ===');
                console.log('Server response:', r);

                if (r.success) {
                // Stop the UI timer
                if (timerInterval) {
                clearInterval(timerInterval);
                timerInterval = null;
                }
                $timerBadge.addClass('d-none');
                isPaused = false;

                // Update button states
                $('#btnTimeStart').prop('disabled', false).find('span').text('Time Start');
                $('#btnTimePause').prop('disabled', true).hide();
                $('#btnTimeStop').prop('disabled', true);

                // Close modal and reset
                $('#timerStopModal').modal('hide');
                $('#timerStopDescription').val('');
                $('#timerAttachments').val('');

                // Show success message
                if (r.attachments_count > 0) {
                alert('Timer stopped successfully with ' + r.attachments_count + ' attachment(s) saved!');
                } else {
                alert('Timer stopped successfully!');
                }

                // Reload page after 1 second
                setTimeout(function() {
                location.reload();
                }, 1000);
                } else {
                alert('Error: ' + (r.error || 'Unknown error'));
                }
                },
                error: function(xhr, status, error) {
                console.error('Timer stop request failed:', status, error);
                alert('Failed to stop timer: ' + error);
                },
                complete: function() {
                // Reset button state
                $('#confirmStopBtn').prop('disabled', false).text('Stop & Save');
                }
                });
                });

                // Delete handlers
                $(document).on('click', '.delete-timesheet-btn, .delete-subtask-btn', function(e) {
                e.preventDefault();
                if (!confirm('Are you sure you want to delete this item?')) return;

                var $form = $(this).closest('form');
                $.ajax({
                url: $form.attr('action'),
                type: 'POST',
                data: $form.serialize(),
                dataType: 'json',
                success: function(result) {
                if (result.success) {
                location.reload();
                } else {
                alert('Failed to delete: ' + (result.error || 'Unknown error'));
                }
                },
                error: function() {
                alert('Failed to delete item');
                }
                });
                });

                // FIXED: Format number inputs - left alignment
                function formatNumberInputs() {
                $('input[type="number"]').on('blur', function() {
                var value = parseFloat($(this).val());
                if (!isNaN(value)) {
                $(this).val(value.toFixed(2));
                }
                });

                // Format hours display
                $('.timesheet-hours').each(function() {
                var hoursVal = parseFloat($(this).text());
                if (!isNaN(hoursVal)) {
                $(this).text(hoursVal.toFixed(2) + ' hours');
                }
                });
                }

                // Initialize format functions
                formatNumberInputs();

                // FIXED: Format existing percentage displays - left aligned
                $('.percentage-input').each(function() {
                var value = parseFloat($(this).val());
                if (!isNaN(value)) {
                $(this).val(value.toFixed(2));
                }
                // Ensure left alignment
                $(this).css('text-align', 'left');
                });

                // FIXED: Ensure all number inputs are left aligned
                $('input[type="number"]').css('text-align', 'left');

                // XML-COMPATIBLE: File preview functionality untuk timer attachments
                $('#timerAttachments').on('change', function() {
                const files = this.files;
                let preview = $('#filePreview');

                if (preview.length === 0) {
                var previewDiv = $('
').attr('id', 'filePreview').addClass('mt-2');
                $(this).after(previewDiv);
                preview = $('#filePreview');
                }

                preview.empty();

                if (files.length > 0) {
                var fileListContainer = $('
');
                var fileLabel = $('').addClass('text-muted').text('Selected files:');
                fileListContainer.append(fileLabel);
                fileListContainer.append($('<br/>'));

                for (let i = 0; i< files.length; i++) {
                var fileBadge = $('')
                .addClass('badge bg-light text-dark me-1')
                .text(files[i].name);
                fileListContainer.append(fileBadge);
                }

                preview.append(fileListContainer);
                }
                });

                // XML-COMPATIBLE: Real-time validation untuk description
                $('#timerStopDescription').on('input', function() {
                const val = $(this).val().trim();
                const errorDiv = $('#descriptionError');
                const stopBtn = $('#confirmStopBtn');

                if (val.length === 0) {
                $(this).addClass('is-invalid');
                if (errorDiv.length > 0) {
                errorDiv.removeClass('d-none');
                }
                stopBtn.prop('disabled', true);
                } else {
                $(this).removeClass('is-invalid');
                if (errorDiv.length > 0) {
                errorDiv.addClass('d-none');
                }
                stopBtn.prop('disabled', false);
                }
                });

                // Make functions globally available
                window.openTimesheetPopup = openTimesheetPopup;
                window.openSubtaskPopup = openSubtaskPopup;
                window.openInvoicePlanPopup = openInvoicePlanPopup;
                window.formatNumberInputs = formatNumberInputs;
                });

                function updateTimesheetHours() {
                var jam = parseInt($('#timesheet_jam').val()) || 0;
                var menit = parseInt($('#timesheet_menit').val()) || 0;
                // Kalkulasi total jam dalam desimal
                var totalJam = jam + (menit / 60);
                $('#timesheet_hours').val(totalJam.toFixed(2));
                // Format tampil
                var str = '';
                if (jam > 0) str += jam + ' jam ';
                if (menit > 0) str += menit + ' menit';
                $('#timesheet_hours_display').text(str.trim() || '0 menit');
                }
                $('#timesheet_jam, #timesheet_menit').on('input change', updateTimesheetHours);
                updateTimesheetHours();

                // PATCH: Saat popup timesheet dibuka, reset jam-menit
                $('#timesheetModal').on('show.bs.modal', function() {
                $('#timesheet_jam').val(0);
                $('#timesheet_menit').val(0);
                updateTimesheetHours();
                });

                // PATCH: TOMBOL FINISH TASK => "Submit"
                $('form').find('button[type="submit"]').each(function() {
                if ($(this).text().includes('Finish Task')) {
                $(this).html('Submit');
                }
                });

                // PATCH: Refresh UI timer badge lebih halus
                function refreshTimerUI() {
                $('#runningTimerBadge').fadeOut(200, function() {
                $(this).fadeIn(300).text('00:00:00').removeClass('bg-warning').addClass('bg-primary');
                });
                }

                // PATCH: Navigasi subtasks, back tetap scope parent
                $('.btn-back-to-parent').on('click', function(e) {
                var parentId = $(this).data('parent-id');
                if (parentId) {
                window.location.href = '/portal/tasks/parent/' + parentId;
                }
                });
